    }
    50% { 
      border-radius: 80% 20% 50% 50% / 75% 25% 75% 25%; 
    }
    75% { 
      border-radius: 20% 80% 80% 20% / 70% 70% 30% 30%; 
    }
  }

  @keyframes rotate-slow {
    from {
      transform: rotate(0deg);
    }
    to {
      transform: rotate(360deg);
    }
  }

  /* Particle animation removed */

  .animate-float {
    animation: float 8s ease-in-out infinite;
  }

  .animate-glow {
    animation: glow 3s ease-in-out infinite alternate;
  }

  .animate-morph {
    animation: morph 8s ease-in-out infinite;
  }

  .animate-rotate-slow {
    animation: rotate-slow 20s linear infinite;
  }

  /* Particle animation class removed */

  /* Glass morphism effect */
  .glass-morphism {
    background: hsla(0 0% 100% / 0.05);
    backdrop-filter: blur(20px);
    border: 1px solid hsla(0 0% 100% / 0.1);
  }

  /* Floating card with 3D effects */
  .floating-card {
    transform-style: preserve-3d;
    transition: all 0.5s cubic-bezier(0.25, 0.46, 0.45, 0.94);
  }

  .floating-card:hover {
    transform: perspective(1000px) rotateX(10deg) rotateY(10deg) translateZ(20px);
  }

  /* 3D text effect */
  .text-3d {
    text-shadow: 0 0 20px hsla(180 100% 50% / 0.5);
  }

  /* Hero background gradient */
  .hero-bg {
    background: 
      radial-gradient(circle at 20% 50%, hsla(180 100% 50% / 0.1) 0%, transparent 50%),
      radial-gradient(circle at 80% 20%, hsla(180 100% 50% / 0.05) 0%, transparent 50%),
      radial-gradient(circle at 40% 80%, hsla(0 0% 100% / 0.02) 0%, transparent 50%);
  }

  /* Particle system removed */

  /* Custom scrollbar */
  ::-webkit-scrollbar {
    width: 8px;
  }

  ::-webkit-scrollbar-track {
    background: var(--deep-black);
  }

  ::-webkit-scrollbar-thumb {
    background: var(--neon-cyan);
    border-radius: 4px;
  }

  ::-webkit-scrollbar-thumb:hover {
    background: hsla(180 100% 50% / 0.8);
  }
}

/* Custom color utilities */
.bg-deep-black {
  background-color: var(--deep-black);
}

.bg-dark-gray {
  background-color: var(--dark-gray);
}

.bg-neon-cyan {
  background-color: var(--neon-cyan);
}

.text-neon-cyan {
  color: var(--neon-cyan);
}

.border-neon-cyan {
  border-color: var(--neon-cyan);
}

.bg-glass-white {
  background-color: var(--glass-white);
}

.border-glass-white {
  border-color: var(--glass-white-border);
}

/* Select component styles for better dark theme support */
[data-radix-select-content] {
  background-color: var(--deep-black) !important;
  border-color: hsl(210 5.2632% 14.9020%) !important;
  box-shadow: 0 10px 38px -10px rgba(22, 23, 24, 0.35), 0 10px 20px -15px rgba(22, 23, 24, 0.2) !important;
}

[data-radix-select-item] {
  color: hsl(0 0% 100%) !important;
}

[data-radix-select-item][data-highlighted] {
  background: linear-gradient(90deg, hsla(180 100% 50% / 0.2) 0%, hsla(180 100% 50% / 0.1) 100%) !important;
  color: hsl(180 100% 50%) !important;
  box-shadow: 0 0 10px hsla(180 100% 50% / 0.2) !important;
  transition: all 0.2s ease-in-out !important;
}

[data-radix-select-item][data-state="checked"] {
  background: linear-gradient(90deg, hsla(180 100% 50% / 0.25) 0%, hsla(180 100% 50% / 0.15) 100%) !important;
  color: hsl(180 100% 50%) !important;
  font-weight: 500 !important;
  box-shadow: 0 0 15px hsla(180 100% 50% / 0.3) !important;
}

[data-radix-select-trigger] [data-placeholder] {
  color: hsl(210 3.3898% 46.2745%) !important;
}

/* Additional admin panel styling fixes */
.bg-gray-800 {
  background-color: hsl(0 0% 12%) !important;
  color: hsl(0 0% 100%) !important;
}

.border-gray-600 {
  border-color: hsl(0 0% 25%) !important;
}

.text-white {
  color: hsl(0 0% 100%) !important;
}

.text-gray-400 {
  color: hsl(0 0% 70%) !important;
}

.text-gray-300 {
  color: hsl(0 0% 80%) !important;
}

/* Form input styling fixes */
input[type="text"], 
input[type="email"], 
input[type="url"],
textarea {
  background-color: hsl(0 0% 12%) !important;
  color: hsl(0 0% 100%) !important;
  border-color: hsl(0 0% 25%) !important;
}

input[type="text"]:focus, 
input[type="email"]:focus, 
input[type="url"]:focus,
textarea:focus {
  border-color: hsl(180 100% 50%) !important;
  box-shadow: 0 0 0 1px hsl(180 100% 50%) !important;
}

/* Tabs styling fixes */
[data-state="active"] {
  background: linear-gradient(90deg, hsl(180 100% 50%) 0%, hsl(200 100% 60%) 100%) !important;
  color: hsl(0 0% 10%) !important;
}

/* Badge styling fixes */
.border-gray-500\/50 {
  border-color: hsl(0 0% 40%) !important;
}

.bg-gray-800\/50 {
  background-color: hsl(0 0% 12%) !important;
}

[data-radix-select-value] {
  color: hsl(0 0% 100%) !important;
}

[data-radix-select-value][data-placeholder] {
  color: hsl(210 3.3898% 46.2745%) !important;
}

/* Additional specific selector for placeholder text */
.select-placeholder {
  color: hsl(210 3.3898% 46.2745%) !important;
}

/* Mobile-specific improvements */
@media (max-width: 768px) {
  .container {
    padding-left: 1rem;
    padding-right: 1rem;
  }

  .text-3d {
    text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.5);
  }

  .glass-morphism {
    backdrop-filter: blur(8px);
    background: rgba(255, 255, 255, 0.08);
  }

  .floating-card {
    transform: none !important;
  }

  /* Ensure text remains readable on mobile */
  .hero-content h1 {
    font-size: 2rem;
    line-height: 1.2;
  }

  .hero-content p {
    font-size: 1rem;
    line-height: 1.5;
  }

  /* Better spacing for mobile */
  section {
    padding-top: 4rem !important;
    padding-bottom: 4rem !important;
  }

  /* Mobile navigation improvements */
  .mobile-menu {
    background: rgba(0, 0, 0, 0.95);
    backdrop-filter: blur(20px);
  }

  /* Adjust button sizes for touch */
  button {
    min-height: 44px;
    min-width: 44px;
  }

  /* Form improvements */
  input, textarea, select {
    font-size: 16px; /* Prevents zoom on iOS */
  }
}

/* Small mobile devices */
@media (max-width: 480px) {
  .container {
    padding-left: 0.75rem;
    padding-right: 0.75rem;
  }

  /* Even smaller text on very small screens */
  .hero-content h1 {
    font-size: 1.75rem;
    line-height: 1.1;
  }

  .hero-content p {
    font-size: 0.9rem;
  }

  /* Reduce section padding further */
  section {
    padding-top: 3rem !important;
    padding-bottom: 3rem !important;
  }
}

